package com.core;

public class DecrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=12;
		int b=--a;
		int c=12;
		int d=c--;
		
				
		System.out.println(a );
		System.out.println(  b);
		System.out.println(c);
		System.out.println(d);

	}

}
