package com.core;

public class StringConcepts {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 
		
		String name1 ="Rama";
		String  name2="GOPI";
		String name3= "Ravi";
		String name4 ="hari";
		String name5= "Murali";
		String name6 ="harikrish";
		String name7= "RAMA";
		 System.out.println(name1.concat(name2));
		 System.out.println(name2.toLowerCase());
		 System.out.println(name4.toUpperCase());
		 System.out.println(name1.equals(name7));
		 System.out.println(name1.equalsIgnoreCase(name7));
		 System.out.println(name1.hashCode());
		 System.out.println(name5.charAt(2));
		 System.out.println(name5.isEmpty());
		 
		
		
		

	}

}
